import { Fragment, useState, useEffect, Component } from 'react';

import Users from './Users';
import classes from './UserFinder.module.css';
import UsersContext from '../store/users-context';

class UserFinder extends Component{
    
    static contextType = UsersContext;

    constructor(){
        super()
        this.state = {
            filteredUsers: this.context.DUMMY_USERS,
            searchTerm: ''
        }
    }

    componentDidMount(){ // Equivalente a useEffect[], solo al iniciar el comp
      //Send http request
      this.setState({ filteredUsers: this.context.DUMMY_USERS })
    }

    componentDidUpdate( prevProps, prevState ){ // Equivalente a useEffect[dependencia]
        if(prevState.searchTerm !== this.state.searchTerm){
            this.setState({filteredUsers: this.context.DUMMY_USERS.filter( user => user.name.includes(this.state.searchTerm))})
        }
    }
    searchChangeHandler = (event) => {
        this.setState({searchTerm: event.target.value})
    };

      render(){
        return (
            <Fragment>
              <div className={classes.finder}>
                <input type='search' onChange={this.searchChangeHandler.bind(this)} />
              </div>
              <Users users={this.state.filteredUsers} />
            </Fragment>
          );
      }
}

